sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }

	if(newValue == ""){
		g_form.setValue("geschaetzte_fahrtkosten", "EUR;" + 0.00);
		return;
	}

    var fahrtkosten = g_form.getValue("geschaetzte_fahrtkosten");
    var fahrtkostenCurrency = fahrtkosten.split(';')[0];
    var fahrtkostenValue = parseFloat((fahrtkosten.split(';')[1]));

    var gaa = new GlideAjax('ExchangeRateScript');
    gaa.addParam('sysparm_name', 'getRate');
    gaa.addParam('sysparm_currency', fahrtkostenCurrency);
    gaa.addParam('sysparm_value', fahrtkostenValue);
    gaa.getXML(RateParse);
}

function RateParse(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
	answer = parseFloat(answer);
	answer = answer.toFixed(2.0);
	answer = parseFloat(answer);

	//var currency_code = 'EUR';
	var fahr = answer;
	g_form.setValue("kalkulierte_fahrkosten", fahr);
	var geb = g_form.getDecimalValue("kalkulierte_geb_hren");
	geb *= 0.01;
    var unt = g_form.getDecimalValue("kalkulierte_unterkunftskosten");
	unt *= 0.01;
    var gesamt = unt + fahr + geb;
	gesamt = gesamt.toFixed(2);
	gesamt = gesamt.replace(".", ",");
	//alert("gebühren: " + geb + "\n" + "unterkunft: " + unt + "\n" + "fahrt: " + fahr + "\n" + "gesamt: " + gesamt);
	g_form.setValue("gesamtkosten1", gesamt);

}
