sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }

    var gebueren = g_form.getValue("gebuehren");
    var gebuerenCurrency = String(gebueren.split(';')[0]);
    var gebuerenValue = parseFloat(gebueren.split(';')[1]);


    var ga = new GlideAjax('ExchangeRateScript');
    ga.addParam('sysparm_name', 'getRate');
    ga.addParam('sysparm_currency', gebuerenCurrency);
    ga.addParam('sysparm_value', gebuerenValue);
    ga.getXML(RateParse);

}

function RateParse(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    //alert(answer);
    var currency_code = 'EUR';
    var fahrW = g_form.getValue("fahrtkosten");
	var fahr = fahrW.split(';')[1];
    var untW = g_form.getValue("unterkunftskosten");
	var unt = untW.split(';')[1];
	//var km = g_form.getValue("fahrtkosten_auto");
	var kmW = g_form.getValue("test");
	var km = kmW.split(';')[1];
    var gesamt = parseFloat(answer) + parseFloat(fahr) + parseFloat(unt) + parseFloat(km);
    g_form.setValue("gesamtkosten", currency_code + ";" + Math.round(gesamt * 100) / 100.0);
}
