sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading) {
      return;
   }

   var currency_code = 'EUR';
    var fahr = g_form.getDecimalValue("fahrtkosten");

    var unt = g_form.getDecimalValue("unterkunftskosten");
	var geb = g_form.getDecimalValue("gebuehren");
	//var km = g_form.getValue("fahrtkosten_auto");
	var kmWert = g_form.getValue("test");
	var km = kmWert.split(';')[1];
	//g_form.addErrorMessage(parseFloat(km));
    var gesamt = parseFloat(fahr) + parseFloat(unt) + parseFloat(geb) + parseFloat(km);
    g_form.setValue("gesamtkosten", currency_code + ";" + Math.round(gesamt * 100) / 100.0);
}
