sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading) {
      return;
   }

	var fahr = g_form.getDecimalValue("kalkulierte_fahrkosten");
    var unt = g_form.getDecimalValue("kalkulierte_unterkunftskosten");
	var geb = g_form.getDecimalValue("kalkulierte_geb_hren");
	var km = g_form.getDecimalValue("kalkulierte_pkw_kosten");
	//var km = kmWert.split(';')[1];
	//g_form.addErrorMessage(parseFloat(km));
    var gesamt = fahr + unt + geb + km;
	var gesamtReise = fahr + km;
	gesamtReise = gesamtReise.toFixed(2);
	gesamtReise = gesamtReise.replace(".", ",");
	gesamt = gesamt.toFixed(2);
	gesamt = gesamt.replace(".", ",");
    g_form.setValue("gesamtkosten1", gesamt);
	g_form.setValue("gesamt_reisekosten", gesamtReise);
}
