sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) { return; }

	if(newValue == ""){
		g_form.setValue("geschaetzte_unterkunftskosten", "EUR;" + 0.00);
	}

	var unterkunftskosten = g_form.getValue("geschaetzte_unterkunftskosten");
	var unterkunftskostenCurrency = unterkunftskosten.split(';')[0];
	var unterkunftskostenValue = parseFloat(unterkunftskosten.split(';')[1]);

	var gaaa = new GlideAjax('ExchangeRateScript');
	gaaa.addParam('sysparm_name', 'getRate');
	gaaa.addParam('sysparm_currency', unterkunftskostenCurrency);
	gaaa.addParam('sysparm_value', unterkunftskostenValue);
	gaaa.getXML(RateParse);
}

function RateParse(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    //alert(answer);
	var currency_code = 'EUR';
	var unt =  parseFloat(answer);
	g_form.setValue("kalkulierte_unterkunftskosten", unt);
	var fahr = g_form.getDecimalValue("kalkulierte_fahrkosten");
	var geb = g_form.getDecimalValue("kalkulierte_geb_hren");
	var gesamt =  Math.round((unt + fahr + geb) * 100) / 100.0;
    g_form.setValue("gesamtkosten", currency_code + ";" + gesamt);
}
