sys_variable_value.value:
Sehr geehrte/r ${name},
Ihre Dienstreise mit der Auftragsnummer: ${auftragsnummer} wurde von ihrem Vorgesetzten abgelehnt.

<mail_script> 
var app = new GlideRecord('sysapproval_approver');   

app.addQuery('sysapproval', current.sys_id);   

app.addQuery('state', 'rejected');   

app.query(); 

while (app.next()) {       
       template.print("Approver: " + app.approver.getDisplayValue() + ":\n");   
       template.print("Comments: " + app.comments.getJournalEntry(-1) + "\n\n");   
} 
</mail_script>

