<mail_script>

  var appr = new GlideRecord('sysapproval_approver');

	appr.addQuery('document_id', current.sys_id);

	appr.addQuery('state', 'rejected');

	appr.query();

while (appr.next()) {

 
var comments = appr.comments.getDisplayValue().toString();

template.print('Aus folgendem Grund: ' + comments + '\n');

  }

</mail_script>

