current.status = 4;
current.auftragsnummer.status = 5;
gs.addInfoMessage(current.auftragsnummer.status);

var gr = new GlideRecord('x_526765_reisemngr_reiseantrag');
gr.get('auftragsnummer', current.auftragsnummer.auftragsnummer);
gs.addInfoMessage("Aktuelle auftragsnummer:" + current.auftragsnummer.auftragsnummer);
gr.setValue("status", 5);
gr.update();

